<?php$banpage = true;only_reg();$set['title'] = 'БАН';include_once 'sys/inc/thead.php';title();err();aut();if ( !isset( $user ) ) {    header( "Location: /index.php?" . SID );    exit;}if ( db::count("SELECT COUNT(*) FROM `ban` WHERE `id_user` = '$user[id]' AND (`time` > '$time' OR `view` = '0')") == 0 ) {    header( 'Location: /index.php?' . SID );    exit;}db::query("UPDATE `ban` SET `view` = '1' WHERE `id_user` = '$user[id]'");$k_post = db::count("SELECT COUNT(*) FROM `ban` WHERE `id_user` = '$user[id]'");$k_page = k_page( $k_post, $set['p_str'] );$page   = page( $k_page );$start  = $set['p_str'] * $page - $set['p_str'];echo "<table class='post'>\n";$q = db::query("SELECT * FROM `ban` WHERE `id_user` = '$user[id]' ORDER BY `time` DESC LIMIT $start, $set[p_str]");while ( $post =  $q->fetch_assoc() ) {    $ank = get_user( $post['id_ban'] );    if ( $num == 0 ) {        echo "  <div class='nav1'>\n";        $num = 1;    } elseif ( $num == 1 ) {        echo "  <div class='nav2'>\n";        $num = 0;    }    echo "Бан выдал" . ( $ank['pol'] == 0 ? "а" : "" ) . " $ank[nick]: ";    if ( $post['navsegda'] == 1 ) {        echo " бан <font color=red><b>навсегда</b></font><br />";    } else {        echo " до " . vremja( $post['time'] ) . "<br />";    }    echo '<b>Причина:</b> ' . $pBan[$post['pochemu']] . '<br />';    echo '<b>Раздел:</b> ' . $rBan[$post['razdel']] . '<br />';    echo '<b>Комментарий:</b> ' . esc( trim( br( bbcode( smiles( links( stripcslashes( htmlspecialchars( $post['prich'] ) ) ) ) ) ) ) ) . "<br />\n";    if ( $post['time'] > $time )        echo "<font color=red><b>Активен</b></font><br />\n";    echo "   </div>\n";}echo "</table>\n";if ( $k_page > 1 )    str( '?', $k_page, $page );echo "Чтобы больше не возникало подобных ситуаций, рекомендуем Вам изучить <a href=\"/rules.php\">правила</a> нашего сайта<br />\n";include_once 'sys/inc/tfoot.php';?>