<?phpif (!isset($user) || db::count("SELECT COUNT(*) FROM `ban` WHERE `user_id` = '$user[id]' AND `time_until` > '" . time() . "'") == 0 ) {    ds_redirect(get_site_url('/'));}$set['title'] = __('Ваш профиль заблокирован'); get_header(); $k_post = db::count("SELECT COUNT(*) FROM `ban` WHERE `user_id` = '$user[id]'");$k_page = k_page( $k_post, $set['p_str'] );$page   = page( $k_page );$start  = $set['p_str'] * $page - $set['p_str'];?><div class="box-group-wrap ds-ban-page"><?$q = db::query("SELECT * FROM `ban` WHERE `user_id` = '$user[id]' AND `time_until` > '" . time() . "' ORDER BY `time_until` DESC LIMIT $start, $set[p_str]");while ( $item =  $q->fetch_assoc() ) {    $info = array(__('Кто заблокировал: %s', get_user_nick($item['banned_id'])));     $info[] = __('Дата блокировки: %s', vremja($item['time_create']));     if ($item['comment']) {        $info[] = __('Комментарий: %s', output_text($item['comment']));     }    ?>    <div class="box-group">        <div class="box-group-title"><?php echo __('Действует до: %s', $item['forever'] == 0 ? vremja($item['time_until']) : __('Навсегда')); ?></div>        <div class="box-group-block">            <?php echo join('<br />', $info); ?>        </div>    </div>    <?}?></div><?get_footer();